import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Route, Router, ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-register-page',
  templateUrl: './register-page.component.html',
  styleUrls: ['./register-page.component.scss']
})
export class RegisterPageComponent {

  registerForm = new FormGroup({
    name: new FormControl('', [Validators.required]),
    lastName: new FormControl('', [Validators.required]),
    secondLastName: new FormControl('', [Validators.required]),
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', [Validators.required, Validators.minLength(8)]),
    confirmPassword: new FormControl('', [Validators.required, Validators.minLength(8)]),
    phone: new FormControl('', [Validators.required, Validators.minLength(10),Validators.maxLength(15)]),
    genre: new FormControl('', [Validators.required]),
    medicaSpecialisation: new FormControl('', [Validators.required]),
    medicalCenter: new FormControl('', [Validators.required]),
  
  });

  constructor(private router: Router, private route: ActivatedRoute) { }

  registerbutton() {
    console.log(this.registerForm.value);
    if(this.registerForm.valid){   //valida que el formulario sea valido y si lo es pasaremos a iniciar sesion
      this.router.navigate(['login', this.registerForm.value]);
    }else{
      alert('Registro invalido');
    }
  }

}

<div class="register-box">
    <form [formGroup]="registerForm" class="login-form">
        <h1 class="register-title">Crear una cuenta</h1>
        <input class="register-input" formControlName="name" type="text" placeholder="Escribe tu nombre">
        <br>
        <input class="register-input" formControlName="lastName" type="text" placeholder="Escribe tu apellido paterno">
        <br>
        <input class="register-input" formControlName="secondLastName" type="text" placeholder="Escribe tu aepllido materno">
        <br>
        <input class="register-input" formControlName="email" type="email" placeholder="Escribe tu correro electronico">
        <br>
        <input class="register-input" formControlName="password" type="text" placeholder="Escribe tu contraseña">
        <br>
        <input class="register-input" formControlName="confirmPassword" type="text" placeholder="Repite tu contraseña">
        <br>
        <input class="register-input" formControlName="phone" type="number" placeholder="Escribe tu numero telefonico">
        <br>
        <input class="register-input" formControlName="genre" type="text" placeholder="Seleciona tu genero">
        <br>
        <input class="register-input" formControlName="medicaSpecialisation" type="text" placeholder="Selecciona tu especialidad medica">
        <br>
        <input class="register-input" formControlName="medicalCenter" type="text" placeholder="Escribe el nombre del centro medico donde trabajas">
        </form>
      </div>
      <div class="register-box"><Button type="submit" class="register-buttom" (click)="registerbutton()">Registrarse</Button>
      <div><a style="padding-left: 20px;" routerLink="/login">Ya tienes una cuenta? Inicia sesion.</a></div>
      </div>
